import React from 'react';
import '../App.css';
import {Navbar, Nav, NavItem, Container, Dropdown, DropdownButton, Button} from 'react-bootstrap';
import {LinkContainer} from 'react-router-bootstrap';


class Header extends React.Component {
	constructor(props) {
		super(props)
		this.state = {
			isLogged: false
		}
	}
	async handleClick() {
		// Đoạn này xử lý code
		// đặt tạm đoạn này
		let account = true;
		if (account) {
			this.setState({isLogged: true});
		}
	}

	async logOut(){
		console.log("Logout");
	}





	render() {
		return (
			<>
				<div className="header-line-wrapper">
					<header className="header-wrapper fixed-top plr100">
						<div id="header" className="main-menu">
							<Navbar collapseOnSelect expand="lg" className="navbar-dark">
								<Container>
									<Navbar.Toggle aria-controls="navbar"/>
									<Navbar.Collapse id="navbar">
										<Nav as='ul'>
											<LinkContainer to="/#">
												<NavItem as='li' className="btn-address">
													{!this.state.isLogged ? <><Button variant="primary" onClick={() => this.handleClick()}><span className="text">Login</span><span className="prepend"></span></Button><span
															className="append"></span></> :
														<DropdownButton align="end" title="Hi admin" id="dropdown-menu-button" className="btn-address">
															<Dropdown.Item eventKey="2" onClick={() => this.logOut()}>Logout</Dropdown.Item>
														</DropdownButton>}
												</NavItem>
											</LinkContainer>
										</Nav>
									</Navbar.Collapse>
								</Container>
							</Navbar>
						</div>
					</header>
				</div>
			</>
		)
	}
}

export default Header;
